mapper.select:
select id, menu_name,permission_value,parent_id,TYPE ,sort
from js03_menu
WHERE status=${1}

mapper.update:
update js03_menu
set menu_name = #{menuName,jdbcType=VARCHAR},
 permission_value = #{permissionValue},
 parent_id = #{parentId}
where id  = #{id}

update js03_menu
set status = ${-1}
WHERE id = #{id}
